import { Link, useParams } from 'react-router-dom';
import { Row, Col, ListGroup, Image, Button, Card } from 'react-bootstrap';
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import axios from 'axios';
import {toast} from 'react-toastify';

const OrderScreen = () => {
    const { id: orderId } = useParams();
    const stripe = useStripe();
    const [order, setOrder] = useState(null);
    const [clientSecret, setClientSecret] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [processing, setProcessing] = useState('');
    const [succeeded, setSucceeded] = useState(false);
    const elements = useElements();

    const { userInfo } = useSelector((state) => state.auth);

    useEffect(() => {
        const fetchOrderDetails = async () => {
            try {
                const { data } = await axios.get(`/api/orders/${orderId}`);
                setOrder(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchOrderDetails();
    }, [orderId]);

    useEffect(() => {
        const createPaymentIntent = async () => {
            try {
                const { data } = await axios.post('/api/config/stripe', { amount: order.totalPrice * 100 });
                setClientSecret(data.clientSecret);
            } catch (err) {
                console.error("Error fetching client secret:", err.message);
            }
        };

        if (order && !order.isPaid) {
            createPaymentIntent();
        }
    }, [order]);

    const handleChange = async (event) => {
        setDisabled(event.empty);
        if (event.error) {
            toast.error(event.error.message);
        }
    };

    const handlePayment = async ev => {
        ev.preventDefault();
        setProcessing(true);

        const payload = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement)
            }
        });

        if (payload.error) {
            toast.error(`Payment failed ${payload.error.message}`);
            setProcessing(false);
        } else {
            setProcessing(false);
            setSucceeded(true);
        }
    };

    // ... rest of your component unchanged
    return (
        isLoading ? <Loader /> : error ? ( <Message variant="danger"/> )
        : (
            <>
            <h1>Order {order._id}</h1>
            <Row>
                <Col md={8}>
                    <ListGroup>
                        <ListGroup.Item>
                            <h2>Delivery</h2>
                            <p>
                                <strong>Name:</strong> {order.user.name}
                            </p>
                            <p>
                                <strong>Email:</strong> {order.user.email}
                            </p>
                            <p>
                                <strong>Address:</strong> {order.deliveryAddress.address}
                            </p>

                            {order.isDelivered ? (
                                <Message variant="success">Delivered on {order.deliveredAt}</Message>
                            ) : (
                                <Message variant="danger">Not Delivered </Message>
                            )}

                        </ListGroup.Item>

                       <ListGroup.Item>
                            <h2>Payment Method</h2>
                            <p>
                                <strong>Method:</strong>
                                {order.paymentMethod}
                            </p>
                            {order.isPaid ? (
                                <Message variant="success">Paid on {order.paidAt}</Message>
                            ) : (
                                <Message variant="danger">Not Paid </Message>
                            )}
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Order Items</h2>
                            <ListGroup variant='flush'>
                                {order.orderItems.map((item, index) => (
                                    <ListGroup.Item key={index}>
                                        <Row>
                                        <Col md={1}>
                                            <Image
                                            src={item.image}
                                            alt={item.name}
                                            fluid
                                            rounded
                                            />
                                        </Col>
                                        <Col>
                                            <Link to={`/product/${item.product}`}>
                                            {item.name}
                                            </Link>
                                        </Col>
                                        <Col md={4}>
                                            {item.qty} x ${item.price} = ${item.qty * item.price}
                                        </Col>
                                        </Row>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>

                </ListGroup.Item>
              </ListGroup>
            </Col>
                <Col md={4}>
                    <Card>
                        <ListGroup variant="flush">
                            <ListGroup.Item>
                                <h2>Order Summary</h2>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Items</Col>
                                    <Col>SEK{order.itemsPrice}</Col>
                                </Row>

                                <Row>
                                    <Col>Delivery</Col>
                                    <Col>SEK{order.deliveryPrice}</Col>
                                </Row>

                                <Row>
                                    <Col>Tax</Col>
                                    <Col>SEK{order.taxPrice}</Col>
                                </Row>

                                <Row>
                                    <Col><strong>Total</strong></Col>
                                    <Col>SEK{order.totalPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            {/* PAY ORDER PLACEHOLDER */}
                            {!order.isPaid && stripe && !loadingStripe ? (
                                        <ListGroup.Item>

                                                <div style={{ marginBottom: "20px" }}>
                                                    <CardElement onChange={handleChange}/>
                                                    <Button onClick={handlePayment} disabled={processing || disabled || succeeded} style={{ marginTop: "10px" }}>
                                                        {loadingPay ? "Processing...":"Pay Now"}
                                                    </Button>
                                                </div>

                                        </ListGroup.Item>
                                    ) : null}


                            {/* MARK AS DELIVERED PLACEHOLDER */ }
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
            </>
        )
      );
};

export default OrderScreen;
