// StripePaymentForm.js

import React from 'react';
import { Form } from 'react-bootstrap';
import { CardNumberElement, CardExpiryElement, CardCvcElement, useStripe, useElements } from "@stripe/react-stripe-js";
import 'font-awesome/css/font-awesome.min.css';
import { toast } from 'react-toastify';
import Message from './Message';
// import '../assets/styles/index.css'

const CARD_ELEMENT_OPTIONS = {
  style: {
    base: {
      iconColor: '#666EE8',
      color: '#31325F',
      fontWeight: '300',
      fontFamily: 'Helvetica Neue, Helvetica, Arial, sans-serif',
      fontSize: '18px',
      fontSmoothing: 'antialiased',
      ':-webkit-autofill': {
        color: '#fce883',
      },
      '::placeholder': {
        color: '#CFD7DF',
      },
    },
  }
};

const StripePaymentForm = () => {

    const handleChange = async (event) => {
        setDisabled(event.empty);
        if (event.error) {
            toast.error(event.error.message);
        }
    };

  return (
    <>
      <Form.Group>
          <Form.Label>Card Number</Form.Label>
          <div className="stripe-input">
              <i className="fa fa-credit-card" aria-hidden="true"></i>
              <CardNumberElement options={CARD_ELEMENT_OPTIONS} onChange={handleChange} />
          </div>
      </Form.Group>

      <Form.Group>
          <Form.Label>Expiry Date</Form.Label>
          <div className="stripe-input">
              <i className="fa fa-calendar" aria-hidden="true"></i>
              <CardExpiryElement options={CARD_ELEMENT_OPTIONS} onChange={handleChange} />
          </div>
      </Form.Group>

      <Form.Group>
          <Form.Label>CVV</Form.Label>
          <div className="stripe-input">
              <i className="fa fa-lock" aria-hidden="true"></i>
              <CardCvcElement options={CARD_ELEMENT_OPTIONS} onChange={handleChange} />
          </div>
      </Form.Group>
    </>
  );
};

export default StripePaymentForm;
