import { PRODUCTS_URL } from "../constants";
import { apiSlice } from "./apiSlice";


export const prodDetailApiSlice = apiSlice.injectEndpoints({
    endpoints: (builder) => ({
        getProdDetail: builder.query({
            query: () => ({
                url: PRODUCTS_URL,
            }),
            keepUnusedDataFor: 5
        })
    }),
});

export const { useGetProdDetailQuery } = prodDetailApiSlice;
