import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, ListGroup, Image, Form, Button, Card } from "react-bootstrap";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { toast } from "react-toastify";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useGetOrderDetailsQuery, usePayOrderMutation, useGetStripeKeyQuery } from "../slices/ordersApiSlice";
import { useSelector } from "react-redux";
import axios from 'axios'

// const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
// if (stripeKey && stripeKey !== process.env.REACT_APP_STRIPE_PUBLIC_KEY) {
//     stripe = loadStripe(stripeKey);
// }

const OrderScreen = () => {

    const { id: orderId } = useParams();
    const elements = useElements()

    const [cardReady, setCardReady] = useState(false);

    const { data: order, refetch, isLoading, error } = 
    useGetOrderDetailsQuery(orderId);

    const [payOrder, { isLoading: loadingPay }] =usePayOrderMutation();

    // const stripeInside = useStripe();


    const { data: stripeKey, isLoading: loadingStripeKey, error: errorStripeKey } = useGetStripeKeyQuery();

    const [stripe, setStripe] = useState(null);

    useEffect(() => {
        let mounted = true;

        const initializeStripe = async () => {
            let stripeInstance;

            if (stripeKey && typeof stripeKey === 'string') {
                stripeInstance = await loadStripe(stripeKey);
            } else if (process.env.REACT_APP_STRIPE_PUBLIC_KEY) {
                stripeInstance = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
            } else {
                console.error("Neither stripeKey from API nor REACT_APP_STRIPE_PUBLIC_KEY from env is available");
                throw new Error("Stripe key is missing!");
            }

            if (mounted) {
                setStripe(stripeInstance);
            }

            console.log("Stripe Key from API:", stripeKey);
            console.log("Stripe Key from Env:", process.env.REACT_APP_STRIPE_PUBLIC_KEY);
        };


        initializeStripe();
    }, [stripeKey]);


    const { userInfo } = useSelector((state) => (state.auth))

    const handlePayment = async ev  => {
        ev.preventDefault();
        // if (!stripe || !elements) {
        //     toast.error("Stripe hasn't loaded yet. Please wait a moment.");
        //     return;
        // }

        // if (!stripe || !elements) {
        //     console.log("Stripe or elements not ready");
        //     return;
        // }

        // const card = elements.getElement(CardElement);
        // if (!card) {
        //     console.log("CardElement not ready or not found");
        //     return;
        // }

            // Get the clientSecret from the backend
        const amountInCents = order.totalPrice * 100;
        let clientSecret;
        try {
            const response = await axios.post('/api/config/stripe', { amount: amountInCents });
            clientSecret = response.data.clientSecret;
        } catch (err) {
            toast.error("Failed to create payment intent. Please try again");
            console.error("Error:", err);
            return;
        }

        const result = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement)
            }

        });

        if(!card) {
            console.log("card element not ready or not found")
        }

        if (result.error) {
            // Inform the customer that there was an error.
            toast.error(result.error.message);

        } else if (result.paymentIntent && result.paymentIntent.status === "succeeded") {

            const paymentData = {
                id: result.token.id,
                status: 'completed',
                update_time: new Date().toISOString(), // You can customize this
                payer: {
                    email_address: userInfo.email
                }
            };
            await payOrder({ orderId, details: paymentData });
            refetch();
            // Send the token to your server (this is where you would call your API to charge the card).
            // You've mentioned a usePayOrderMutation, so you'd use that here with the token as an argument.
            // payOrder(result.token);
        }


    }

    // Render logic to improve readability.
    if (isLoading || loadingStripeKey) return <Loader />;
    if (error || errorStripeKey) 
    return <Message variant="danger">{error || errorStripeKey}</Message>;

  return (
    isLoading ? <Loader /> : error ? ( <Message variant="danger"/> )
    : (
        <>
        <h1>Order {order._id}</h1>
        <Row>
            <Col md={8}>
                <ListGroup>
                    <ListGroup.Item>
                        <h2>Delivery</h2>
                        <p>
                            <strong>Name:</strong> {order.user.name}
                        </p>
                        <p>
                            <strong>Email:</strong> {order.user.email}
                        </p>
                        <p>
                            <strong>Address:</strong> {order.deliveryAddress.address}
                        </p>

                        {order.isDelivered ? (
                            <Message variant="success">Delivered on {order.deliveredAt}</Message>
                        ) : (
                            <Message variant="danger">Not Delivered </Message>
                        )}

                    </ListGroup.Item>

                   <ListGroup.Item>
                        <h2>Payment Method</h2>
                        <p>
                            <strong>Method:</strong>
                            {order.paymentMethod}
                        </p>
                        {order.isPaid ? (
                            <Message variant="success">Paid on {order.paidAt}</Message>
                        ) : (
                            <Message variant="danger">Not Paid </Message>
                        )}
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <h2>Order Items</h2>
                        <ListGroup variant='flush'>
                            {order.orderItems.map((item, index) => (
                                <ListGroup.Item key={index}>
                                    <Row>
                                    <Col md={1}>
                                        <Image
                                        src={item.image}
                                        alt={item.name}
                                        fluid
                                        rounded
                                        />
                                    </Col>
                                    <Col>
                                        <Link to={`/product/${item.product}`}>
                                        {item.name}
                                        </Link>
                                    </Col>
                                    <Col md={4}>
                                        {item.qty} x ${item.price} = ${item.qty * item.price}
                                    </Col>
                                    </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>

            </ListGroup.Item>
          </ListGroup>
        </Col>
            <Col md={4}>
                <Card>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Order Summary</h2>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Items</Col>
                                <Col>SEK{order.itemsPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Delivery</Col>
                                <Col>SEK{order.deliveryPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Tax</Col>
                                <Col>SEK{order.taxPrice}</Col>
                            </Row>

                            <Row>
                                <Col><strong>Total</strong></Col>
                                <Col>SEK{order.totalPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        {/* PAY ORDER PLACEHOLDER */}
                        {!order.isPaid && stripe && !loadingStripeKey ? (
                                    <ListGroup.Item>
                                        <Elements stripe={stripe}>
                                            <div style={{ marginBottom: "20px" }}>
                                                <CardElement onReady={() => setCardReady(true)} />
                                                <Button onClick={handlePayment} disabled={!cardReady} style={{ marginTop: "10px" }}>
                                                    {loadingPay ? "Processing...":"Pay Now"}
                                                </Button>
                                            </div>
                                        </Elements>
                                    </ListGroup.Item>
                                ) : null} 


                        {/* MARK AS DELIVERED PLACEHOLDER */ }
                    </ListGroup>
                </Card>
            </Col>
        </Row>
        </>
    )
  );
}

export default OrderScreen;
