import { useDispatch, useSelector } from "react-redux";
import { useCreateOrderMutation } from "../slices/ordersApiSlice";
import { useNavigate } from 'react-router-dom'
import { Elements, CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";


const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const PlaceOrderScreen = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const cart = useSelector(state => state.cart);  // assuming you have cart details in your redux store
    const { cartItems, deliveryAddress, paymentMethod, itemsPrice, taxPrice, deliveryPrice, totalPrice } = cart;

    const [createOrder] = useCreateOrderMutation();

    const stripe = useStripe();
    const elements = useElements();

    const placeOrderHandler = async () => {
        if (!stripe || !elements) {
            return;
        }
        const card = elements.getElement(CardElement);
        const result = await stripe.createToken(card);
        if (result.error) {
            console.error(result.error.message);
        } else {
            try {
                await createOrder({
                    orderItems: cartItems,
                    deliveryAddress,
                    paymentMethod,
                    itemsPrice,
                    taxPrice,
                    deliveryPrice,
                    totalPrice,
                    stripeToken: result.token.id
                });
                navigate.push('/order-success');  // navigate to a successful order screen or confirmation screen
            } catch (error) {
                console.error("Error placing the order:", error);
            }
        }
    };

    return (
        <div>
            <h1>Place Order</h1>
            <div>
                <h2>Order Items</h2>
                {cartItems.map(item => (
                    <div key={item._id}>
                        {item.name} - {item.price}
                    </div>
                ))}
            </div>
            <div>
                <h2>Summary</h2>
                <p>Items Price: ${itemsPrice}</p>
                <p>Tax: ${taxPrice}</p>
                <p>Delivery: ${deliveryPrice}</p>
                <p><strong>Total: ${totalPrice}</strong></p>
            </div>
            <div>
                <h2>Payment</h2>
                <Elements stripe={stripePromise}>
                    <CardElement />
                    <button onClick={placeOrderHandler}>Pay Now</button>
                </Elements>
            </div>
        </div>
    );
}

export default PlaceOrderScreen;
