import { useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { useGetProductDetailsQuery } from '../slices/productsApiSlice';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button,  } from 
'react-bootstrap';

const ProductScreen = () => {
    const { id: productId } = useParams();
    const { data: product, isLoading, error } = useGetProductDetailsQuery(productId);

    // useEffect(() => {
    //     const fetchProduct = async () => {
    //     const {data} = await axios.get(`/api/products/${productId}`);
    //     setProduct(data);
    //     };
    //     fetchProduct();
    // }, [productId])

  return (<>
    <Link className='btn btn-light my-3' to='/order'>Go Back</Link>

    <Message variant='danger'>
          fuck you
          </Message>

    {isLoading ? (
        <Loader/>
    ): error ? (
        <Message variant='danger'>
          {error?.data?.message || error.error}
          </Message>
    ) : (<>

        <Row>
            <Col md={5}>
                <Image src={product.image} alt={product.name}fluid />
            </Col>
            <Col md={4}>
                <ListGroup>
                    <ListGroup.Item>
                        <h3>{product.name}</h3>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        price:SEK {product.price}
                    </ListGroup.Item>
                    <ListGroup.Item>
                        {product.description}
                    </ListGroup.Item>
                </ListGroup>
            </Col>
            <Col md={3}>
                <Card>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                        <Row>
                            <Col>
                            <Col>Price</Col>
                                <strong>SEK {product.price}</strong>
                            </Col>
                        </Row>
                        </ListGroup.Item>
                        <ListGroup.Item>
                        <Row>
                            <Col>
                            <Col>Status</Col>
                                <strong>{product.stock === true ? "Available" : "Sold Out"}</strong>
                            </Col>
                        </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Button
                            className='btn-block'
                            type='button'
                            disabled={product.stock === false}
                            >
                            Add To Cart 

                            </Button>
                        </ListGroup.Item>

                    </ListGroup>
                </Card>
            </Col>
        </Row>
    </>
    )}

    </>
  )
}

export default ProductScreen
