import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, ListGroup, Image, Form, Button, Card, ListGroupItem } from "react-bootstrap";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { toast } from "react-toastify";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useGetOrderDetailsQuery, usePayOrderMutation, useGetStripeKeyQuery } from "../slices/ordersApiSlice";
import { useSelector } from "react-redux";

// const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
// if (stripeKey && stripeKey !== process.env.REACT_APP_STRIPE_PUBLIC_KEY) {
//     stripe = loadStripe(stripeKey);
// }

const OrderScreen = () => {
    const { id: orderId } = useParams();

    const { data: order, refetch, isLoading, error } = 
    useGetOrderDetailsQuery(orderId);

    const [payOrder, { isLoading: loadingPay }] =usePayOrderMutation();

    const stripeInside = useStripe();
    const element = useElements()

    const { data: stripeKey, isLoading: loadingStripeKey, error: errorStripeKey } = useGetStripeKeyQuery();

    const [stripe, setStripe] = useState(null);

    useEffect(() => {
        let mounted = true; // Flag to prevent setting state on an unmounted component

        const initializeStripe = async () => {
            try {
               let stripeInstance;
               if (stripeKey) {
                stripeInstance =  await loadStripe(stripeKey);
               } else {
                stripeInstance = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
               }
               if (mounted) {
                setStripe(stripeInstance);
               }
            } catch (err) {
                console.error("Failed to initialize Stripe:", err)
            }
        };
        initializeStripe();

        return () => {
            mounted = false; // Component cleanup
        }
    }, [stripeKey])


    const { userInfo } = useSelector((state) => (state.auth))

    console.log(order)
  return (
    isLoading ? <Loader /> : error ? ( <Message variant="danger"/> )
    : (
        <>
        <h1>Order {order._id}</h1>
        <Row>
            <Col md={8}>
                <ListGroup>
                    <ListGroup.Item>
                        <h2>Delivery</h2>
                        <p>
                            <strong>Name:</strong> {order.user.name}
                        </p>
                        <p>
                            <strong>Email:</strong> {order.user.email}
                        </p>
                        <p>
                            <strong>Address:</strong> {order.deliveryAddress.address}
                        </p>

                        {order.isDelivered ? (
                            <Message variant="success">Delivered on {order.deliveredAt}</Message>
                        ) : (
                            <Message variant="danger">Not Delivered </Message>
                        )}

                    </ListGroup.Item>

                    <ListGroup.Item>
                        <h2>Payment Method</h2>
                        <p>
                            <strong>Method:</strong>
                            {order.paymentMethod}
                        </p>
                        {order.isPaid ? (
                            <Message variant="success">Paid on {order.paidAt}</Message>
                        ) : (
                            <Message variant="danger">Not Paid </Message>
                        )}
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <h2>Order Items</h2>
                        <ListGroup variant='flush'>
                            {order.orderItems.map((item, index) => (
                                <ListGroup.Item key={index}>
                                    <Row>
                                    <Col md={1}>
                                        <Image
                                        src={item.image}
                                        alt={item.name}
                                        fluid
                                        rounded
                                        />
                                    </Col>
                                    <Col>
                                        <Link to={`/product/${item.product}`}>
                                        {item.name}
                                        </Link>
                                    </Col>
                                    <Col md={4}>
                                        {item.qty} x ${item.price} = ${item.qty * item.price}
                                    </Col>
                                    </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>

            </ListGroup.Item>
          </ListGroup>
        </Col>
            <Col md={4}>
                <Card>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Order Summary</h2>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Items</Col>
                                <Col>SEK{order.itemsPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Delivery</Col>
                                <Col>SEK{order.deliveryPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Tax</Col>
                                <Col>SEK{order.taxPrice}</Col>
                            </Row>

                            <Row>
                                <Col><strong>Total</strong></Col>
                                <Col>SEK{order.totalPrice}</Col>
                            </Row>
                        </ListGroup.Item>
                        {/* PAY ORDER PLACEHOLDER */}
                        {/* MARK AS DELIVERED PLACEHOLDER */}
                    </ListGroup>
                </Card>
            </Col>
        </Row>
        </>
    )
  )
}

export default OrderScreen;
