import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { Row, Col, ListGroup, Image, Card } from "react-bootstrap";
import { loadStripe } from "@stripe/stripe-js";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { toast } from "react-toastify";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useGetOrderDetailsQuery, usePayOrderMutation, useGetStripeKeyQuery } from "../slices/ordersApiSlice";

const OrderScreen = () => {
    const dispatch = useDispatch();
    const { id: orderId } = useParams();
    const stripe = useStripe();
    const elements = useElements();
    const { userInfo } = useSelector((state) => state.auth);

    const { data: order, refetch, isLoading, error } = useGetOrderDetailsQuery(orderId);
    const [payOrder, { isLoading: loadingPay }] = usePayOrderMutation();
    const { data: stripeKey, isLoading: loadingStripeKey, error: errorStripeKey } = useGetStripeKeyQuery();

    const [clientSecret, setClientSecret] = useState("");
    const [disabled, setDisabled] = useState(true);
    const [succeeded, setSucceeded] = useState(false);
    const [processing, setProcessing] = useState(false);

    useEffect(() => {
        if (order && !order.isPaid) {
            dispatch(createPaymentIntent(order.totalPrice)) // Assuming total price is needed to create a PaymentIntent
                .then(result => {
                    setClientSecret(result.payload.clientSecret);
                })
                .catch(err => {
                    toast.error("Failed to create payment intent");
                });
        }
    }, [order, dispatch]);

    useEffect(() => {
        if (stripeKey && typeof stripeKey === 'string') {
            loadStripe(stripeKey);
        } else if (process.env.REACT_APP_STRIPE_PUBLIC_KEY) {
            loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);
        } else {
            console.error("Stripe key is missing!");
        }
    }, [stripeKey]);

    const handleChange = async (event) => {
        setDisabled(event.empty);
        if (event.error) {
            toast.error(event.error.message);
        }
    };

    const handlePayment = async (ev) => {
        ev.preventDefault();
        setProcessing(true);

        const card = elements.getElement(CardElement);

        if (!stripe || !elements || !card) {
            toast.error("Stripe hasn't loaded yet. Please wait a moment.");
            return;
        }

        const result = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: card,
                billing_details: {
                    name: order.user.name,
                    email: order.user.email,
                }
            }
        });

        if (result.error) {
            toast.error(result.error.message);
            setProcessing(false);
        } else if (result.paymentIntent && result.paymentIntent.status === "succeeded") {
            setSucceeded(true);
            const paymentData = {
                id: result.token.id,
                status: 'completed',
                update_time: new Date().toISOString(),
                payer: {
                    email_address: userInfo.email
                }
            };
            await payOrder({ orderId, details: paymentData });
            refetch();
        }
    };

    if (isLoading || loadingStripeKey) return <Loader />;
    if (error || errorStripeKey) return <Message variant="danger">{error || errorStripeKey}</Message>;


      return (
    isLoading ? <Loader /> : error ? ( <Message variant="danger"/> )
    : (
        <>
        <h1>Order {order._id}</h1>
        <Row>
            <Col md={8}>
                <ListGroup>
                    <ListGroup.Item>
                        <h2>Delivery</h2>
                        <p>
                            <strong>Name:</strong> {order.user.name}
                        </p>
                        <p>
                            <strong>Email:</strong> {order.user.email}
                        </p>
                        <p>
                            <strong>Address:</strong> {order.deliveryAddress.address}
                        </p>

                        {order.isDelivered ? (
                            <Message variant="success">Delivered on {order.deliveredAt}</Message>
                        ) : (
                            <Message variant="danger">Not Delivered </Message>
                        )}

                    </ListGroup.Item>

                   <ListGroup.Item>
                        <h2>Payment Method</h2>
                        <p>
                            <strong>Method:</strong>
                            {order.paymentMethod}
                        </p>
                        {order.isPaid ? (
                            <Message variant="success">Paid on {order.paidAt}</Message>
                        ) : (
                            <Message variant="danger">Not Paid </Message>
                        )}
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <h2>Order Items</h2>
                        <ListGroup variant='flush'>
                            {order.orderItems.map((item, index) => (
                                <ListGroup.Item key={index}>
                                    <Row>
                                    <Col md={1}>
                                        <Image
                                        src={item.image}
                                        alt={item.name}
                                        fluid
                                        rounded
                                        />
                                    </Col>
                                    <Col>
                                        <Link to={`/product/${item.product}`}>
                                        {item.name}
                                        </Link>
                                    </Col>
                                    <Col md={4}>
                                        {item.qty} x ${item.price} = ${item.qty * item.price}
                                    </Col>
                                    </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>

            </ListGroup.Item>
          </ListGroup>
        </Col>
            <Col md={4}>
                <Card>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Order Summary</h2>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Items</Col>
                                <Col>SEK{order.itemsPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Delivery</Col>
                                <Col>SEK{order.deliveryPrice}</Col>
                            </Row>

                            <Row>
                                <Col>Tax</Col>
                                <Col>SEK{order.taxPrice}</Col>
                            </Row>

                            <Row>
                                <Col><strong>Total</strong></Col>
                                <Col>SEK{order.totalPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        {/* PAY ORDER PLACEHOLDER */}
                        {!order.isPaid && (
            <form onSubmit={handlePayment}>
                <CardElement onChange={handleChange} />
                <button type="submit" disabled={processing || disabled || succeeded}>
                    {processing ? "Processing..." : "Pay Now"}
                </button>
            </form>
        )}


                        {/* MARK AS DELIVERED PLACEHOLDER */ }
                    </ListGroup>
                </Card>
            </Col>
        </Row>
        </>
    )
  );
}

export default OrderScreen;
